            center : [lng, lat],
            zoom: zoom
        });
        map.on('load', () => {
            let site_id = 0

            var fetch_Requests = setInterval(function getTechnicianData(){

            let url = "http://localhost:5000/api/v1/solar_farms/" + site_id + "/technicians";
            //fetch("http://localhost:5000/api/v1/solar_farms/0/technicians")
            fetch(url)
                .then(res => res.json().then(data => ({status: res.status, body: data})))
                //.then(obj => console.log(obj.body))
