import React, { useRef, useEffect, useState } from 'react';
import ReactDOM from 'react-dom';

import mapboxgl from 'mapbox-gl/dist/mapbox-gl-csp'
import MapboxWorker from 'worker-loader!mapbox-gl/dist/mapbox-gl-csp-worker'

const fetch = require('node-fetch');
mapboxgl.workerClass = MapboxWorker
mapboxgl.accessToken = "pk.eyJ1IjoiZWhybGljaGoiLCJhIjoiY2ttdXNnNmh4MG1kajJ2cWVmbzBoZDZldSJ9.0ffP7axpAqza9qSwgm4vaA"

const Map = () => {
    const mapContainer = useRef();

    const[lng,setLng] = useState(-70.9)
    const[lat,setLat] = useState(42.35)
    const[zoom, setZoom] = useState(12)
    var current_markers = []

    useEffect(() =>{
        const map = new mapboxgl.Map({
            container : mapContainer.current,
            style : 'mapbox://styles/mapbox/streets-v11',
            center : [lng, lat],
            zoom: zoom
        });
        map.on('load', () => {
            let site_id =11

            var fetch_Requests = setInterval(function getTechnicianData(){

            let url = "http://localhost:5000/api/v1/solar_farms/" + site_id + "/technicians";           
            fetch(url)
                .then(res => res.json().then(data => ({status: res.status, body: data})))
                .then(obj => {
                    if(!obj.body.valid_site_id){
                        let ret = "Invalid Site ID";
                        return ret
                    }else{
                        return obj.body
                    }
                })
                .then(geoData => {
                    console.log(geoData);
                    let technicians_center = [0,0]
                    if(geoData != "Invalid Site ID"){
                        let geoDataFeatures = geoData.features;
                        while(current_markers.length > 0){
                            let marker_to_remove = current_markers.pop()
                            marker_to_remove.remove();
                        }
                        for(var i = 0; i<geoDataFeatures.length; i++){
                            let marker = new mapboxgl.Marker({
                                offset: [-120, 0],
                                rotation: geoDataFeatures[i].properties.bearing
                            }).setLngLat(geoDataFeatures[i].geometry.coordinates).addTo(map);

                            current_markers.push(marker)

                            technicians_center[0] += geoDataFeatures[i].geometry.coordinates[0]
                            technicians_center[1] += geoDataFeatures[i].geometry.coordinates[1]
                        }

                        technicians_center[0] /= geoDataFeatures.length
                        technicians_center[1] /= geoDataFeatures.length

                        console.log(technicians_center)
                        map.flyTo({
                            center: technicians_center,
                            speed:  0.5,
                            zoom: 12
                        })
                        let dangerous_techncians = chechTechncianDistance(geoData, 304.8)
                        console.log(dangerous_techncians);
                        if(dangerous_techncians.length > 0){
                            //console.l
                            //alert("Unsafe Conditions\nSite: 3\nTechncians: ", + geoData.dangerous_technicians);
                        }
                        console.log(geoData.dangerous_technicians)
                    }

                })
                .catch(err => console.log("Error: ", err))
                site_id += 1;
            }, 10000)

        });


        map.addControl(new mapboxgl.NavigationControl(), 'bottom-right');
        map.on('move', () =>{
            setLng(map.getCenter().lng.toFixed(4));
            setLat(map.getCenter().lat.toFixed(4));
            setZoom(map.getZoom().toFixed(2));
        });
        return () => map.remove();
    }, []);

    function checkTechnicanDistance(data, min_distance, res){

        let dangerous_technicians = new Set()
        const R = 6371e3
        dataFeatures = data.features
        //console.log(data.length)
        for(var t1=0; t1<dataFeatures.length; t1++){
            for(var t2=0; t2<dataFeatures.length; t2++){

                //console.log("t1: ", t1, " t2L ",t2)
                if(t1 != t2){
                    let technician1 = dataFeatures[t1]
                    let technician2 = dataFeatures[t2]

                    let technician1_time = technician1.properties.tsecs
                    let technician2_time = technician2.properties.tsecs


                    if(technician1_time == technician2_time){
                        //console.log(technician1.gemonetry)
                        let technician1_lon = technician1.geometry.coordinates[0]
                        let techncian2_lon = technician2.geometry.coordinates[0]

                        let technician1_lat = technician1.geometry.coordinates[1]
                        let technician2_lat = technician2.geometry.coordinates[1]

                        let technician1_lat_radians = technician1_lat * Math.PI/180
                        let technician2_lat_radians = technician2_lat * Math.PI/180

                        let delta_lat_radians = (technician2_lat - technician1_lat) * Math.PI/180
                        let delta_lon_radians = (techncian2_lon - technician1_lon) * Math.PI/180

                        let a = Math.sin(delta_lat_radians/2)  * Math.sin(delta_lat_radians) + 
                                Math.cos(technician1_lat_radians) * Math.cos(technician2_lat_radians) *
                                Math.sin(delta_lon_radians/2) * Math.sin(delta_lon_radians);

                        let c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));

                        let distance = R * c
                        console.log(distance)
                        if(distance < min_distance){
                            console.log("UNSAFE DISTANCE");
                            dangerous_technicians.add(technician1.properties.name, technician2.properties.name);
                        }

                    }



                }
            }
        }
        return dangerous_technicians;
    }

    return(
        <div>
            <div className ="sidebar">
                Longitude: {lng} | Latitude: {lat} | Zoom: {zoom}
            </div>
            <div className="map-container" ref={mapContainer} />
        </div>
    );
}
ReactDOM.render(<Map />, document.getElementById('app'));
